import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Form from 'react-bootstrap/Form';

function FormTextExample({ onFormChange }) {
  return (
    <>
      <Form.Control
        id="inputPassword5"
        placeholder="Search for names"
        onChange={(e) => {
          onFormChange(e);
        }}
      />
    </>
  );
}

function SearchResult({ v }) {
  return (
    <div>
      {v}
    </div>
  );
}

function App() {
  const [searchedForName, setSearchedForName] = useState(null);
  function handleFormChange(inputText) {
    setSearchedForName(inputText);
  }
  return (
    <div className="App">
      <body>
        <h3>Learn how to pronounce people's names correctly</h3>
        <FormTextExample onFormChange={handleFormChange}/>
        <div>
          <SearchResult v={searchedForName.value}/>
        </div>
      </body>
    </div>
  );
}

export default App;
