import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Alert from 'react-bootstrap/Alert';
import Card from 'react-bootstrap/Card';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Stack from 'react-bootstrap/Stack';

const namesData = {
  "D'Arcy" : {
    "transcription" : "ˈdɐːsiː"
  },
  "Šimun" : {
    "transcription" : "ˈʂimun"
  }
}

const lookAlikes = {
  "Simun" : "Šimun"
}

const segmentDescriptions = {
  "ˈ" : "placed before the syllable with primary stress",
  "d" : "like the 'd' in 'dog'",
  "ɐː" : "like the 'a' in 'bath'",
  "s" : "like the 's' in 'say'",
  "iː" : "like the 'ee' in 'see'",
}

function SearchInput({ formValue, onFormChange }) {
  return (
    <Form>
      <Form.Group>
        <Form.Control
          id="inputPassword5"
          placeholder="Search for names"
          value={formValue}
          onChange={(e) => {
            onFormChange(e.target.value);
          }}
        />
      </Form.Group>
    </Form>
  );
}

function Spellingsuggestion({ suggestedName, onClickHandler }) {
  return (
    <span onClick={(e) => {
      onClickHandler(suggestedName);
    }} className="spelling-suggestion">
      {suggestedName}
    </span>
  )
}

function segmentise(nameString) {
  let segments = [];
  let holdingCharacters = [];
  for (const character of nameString.split("").reverse().join("")) {
    if (character == "ː") {
      holdingCharacters.push(character);
    }
    else {
      if (holdingCharacters.length > 0) {
        segments.push(character.concat('', holdingCharacters.join()));
        holdingCharacters = [];
      }
      else {
        segments.push(character);
      }
    }
  }
  return segments.toReversed()
}

function SearchResult({ v, suggestedNameOnClickHandler }) {
  if (v == "") {
    return <div/>
  }
  else if (v in namesData) {
    let segmentDivs = [];
    const segments = segmentise(namesData[v]["transcription"]).forEach((s) => {
      segmentDivs.push(<p>{s} {segmentDescriptions[s]}</p>)
    });
    return (
      <Card>
        <Card.Body>
          <Card.Title>
            [{namesData[v]["transcription"]}]
          </Card.Title>
          <Card.Text>
            {segmentDivs}
          </Card.Text>
        </Card.Body>
      </Card>
    )
  }
  else if (v in lookAlikes) {
    return (
      <div>
        "{v}" isn't in the database. Did you mean <Spellingsuggestion suggestedName={lookAlikes[v]} onClickHandler={suggestedNameOnClickHandler}/>?
      </div>
    )
  }
  return (
    <Alert variant="secondary">
      Unfortunately this name does not exist in the database yet.
    </Alert>
  );
}

function App() {
  const [searchedForName, setSearchedForName] = useState('');
  function handleFormChange(inputString) {
    setSearchedForName(inputString);
  }
  return (
    <div className="App">
      <div className="app-body">
        <Stack gap={3}>
            <div><h3>Learn how to pronounce people's names correctly</h3></div>
            <SearchInput formValue={searchedForName} onFormChange={handleFormChange}/>
            <SearchResult v={searchedForName} suggestedNameOnClickHandler={handleFormChange}/>
        </Stack>
      </div>
    </div>
  );
}

export default App;
