import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Form from 'react-bootstrap/Form';

const namesData = {
  "D'Arcy" : {
    "transcription" : "ˈdɐːsiː"
  }
}

function FormTextExample({ onFormChange }) {
  return (
    <>
      <Form.Control
        id="inputPassword5"
        placeholder="Search for names"
        onChange={(e) => {
          onFormChange(e.target.value);
        }}
      />
    </>
  );
}

function SearchResult({ v }) {
  if (v in namesData) {
    return (
      <div>
        {namesData["transcription"]}
      </div>
    )
  }
  return (
    <div>
      Unfortunately this name does not exist in the database yet.
    </div>
  );
}

function App() {
  const [searchedForName, setSearchedForName] = useState(null);
  function handleFormChange(inputText) {
    setSearchedForName(inputText);
  }
  return (
    <div className="App">
      <body>
        <h3>Learn how to pronounce people's names correctly</h3>
        <FormTextExample onFormChange={handleFormChange}/>
        <SearchResult v={searchedForName}/>
      </body>
    </div>
  );
}

export default App;
