import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Form from 'react-bootstrap/Form';

const namesData = {
  "D'Arcy" : {
    "transcription" : "ˈdɐːsiː"
  },
  "Šimun" : {
    "transcription" : "ˈʂimun"
  }
}

const lookAlikes = {
  "Simun" : "Šimun"
}

function SearchInput({ onFormChange }) {
  return (
    <>
      <Form.Control
        id="inputPassword5"
        placeholder="Search for names"
        onChange={(e) => {
          onFormChange(e.target.value);
        }}
      />
    </>
  );
}

function Spellingsuggestion({ suggestedName, onClickHandler }) {
  return (
    <span onclick={onClickHandler}>{suggestedName}</span>
  )
}

function SearchResult({ v, suggestNameOnClickHandler }) {
  if (v === null) {
    return <div/>
  }
  else if (v in namesData) {
    return (
      <div>
        [{namesData[v]["transcription"]}]
      </div>
    )
  }
  else if (v in lookAlikes) {
    return (
      <div>
        "{v}" isn't in the database. Did you mean "<Spellingsuggestion suggestedName={lookAlikes[v]}/>"?
      </div>
    )
  }
  return (
    <div>
      Unfortunately this name does not exist in the database yet.
    </div>
  );
}

function App() {
  const [searchedForName, setSearchedForName] = useState(null);
  function handleFormChange(inputText) {
    setSearchedForName(inputText);
  }
  return (
    <div className="App">
      <body>
        <h3>Learn how to pronounce people's names correctly</h3>
        <SearchInput onFormChange={handleFormChange}/>
        <SearchResult v={searchedForName} suggestNameOnClickHandler={handleFormChange}/>
      </body>
    </div>
  );
}

export default App;
